import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../CSS/Style.css';
import {Link,useParams} from "react-router-dom";
import Category from './Category';

function Api() {

    const [apidata,setApidata]=useState([]);
    const[categories,setCategories]=useState([]);



    useEffect(()=>{

      axios.get("https://fakestoreapi.com/products")
      .then((response)=>{
        console.log(response.data);
        setApidata(response.data);
      })
    },[])

    const changecategory = (cat)=>{
      const update=Api.categories((x)=>x.category===cat);
      setCategories(update);
    }
    return(
        <>

              <button onClick={()=>changecategory("jewelery")}>Jewelery</button>
               <button onClick={()=>changecategory("electronics")}>electronics</button>
               <button onClick={()=>changecategory("women's Clothing")}>women's Clothing</button>
               <button onClick={()=>changecategory("Men's Clothing")}>Men's Clothing</button>
      <div className='page'>

      <div className='products'>
        {
          apidata && apidata.length>0 && apidata.map((products)=>{
            console.log(products);

            return(
              <>
               <button onClick={()=>changecategory("jewelery")}>Jewelery</button>
               <button onClick={()=>changecategory("electronics")}>electronics</button>
               <button onClick={()=>changecategory("women's Clothing")}>women's Clothing</button>
               <button onClick={()=>changecategory("Men's Clothing")}>Men's Clothing</button>
                <div className='product'>
               <div className='cards'>

               <Link to={`/${products.id}`}>
                <img src={products?.image} alt='image' className='images'></img></Link>
               <p>{products.title}</p>
               <p>Rs : {products.price}</p>
               <button className='button'>ADD TO CARD</button>
               </div>
               </div>
              </>
            )
          })
        }
      </div>
      </div>
      </>

    )


}

export default Api;
