import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../CSS/Api.css';
import {Link} from "react-router-dom";

function Api() {

    const [apidata,setApidata]=useState([]);
    // const [loading,setLoading]=useState(false);

    useEffect(()=>{
      // setLoading(true);
      axios.get("https://fakestoreapi.com/products")
      .then((response)=>{
        console.log(response.data);
        setApidata(response.data);
        // setLoading(false);
      })
      // .catch((e)=>console.log(e))
    },[])
    return(

      <div className='products'>
        {
          apidata && apidata.length>0 && apidata.map((products)=>{
            console.log(products);

            return(
              <>
                <div className=''>
               <div className='cards'>
               <Link to="/Details"><img src={products?.image} alt='image' className='images'></img></Link>
               <p>{products.title}</p>
               <p>{products.price}</p>

               {/* <a href="https://fakestoreapi.com/products/1"><img src={data.image} className='images'></img></a> */}
               </div>
               </div>
              </>
            )
          })
        }
      </div>

    )


}

export default Api;
