import axios from 'axios';
import '../CSS/Style.css';
import {Link,useParams} from "react-router-dom";

function Api() {

    const [apidata,setApidata]=useState([]);


    useEffect(()=>{

      axios.get("https://fakestoreapi.com/products")
      .then((response)=>{
        console.log(response.data);
        setApidata(response.data);
      })
    },[])
    return(
        <>
      <div className='page'>
      {/* <Link to={/category/jewelery}>
      <button className='button'>all</button></Link>
      <Link to={/category/jewelery}>
      <button className='button'>jewelery</button></Link>
      <button className='button'>electronics</button>
      <button className='button'>Men's Clothing</button>
      <button className='button'>women's clothing</button> */}
      <button className='button'></button>
      <div className='products'>
        {
          apidata && apidata.length>0 && apidata.map((products)=>{
            console.log(products);

            return(
              <>
                <div className='product'>
               <div className='cards'>

               <Link to={`/${products.id}`}>
                <img src={products?.image} alt='image' className='images'></img></Link>
               <p>{products.title}</p>
               <p>Rs : {products.price}</p>
               <button className='button'>ADD TO CARD</button>
               </div>
               </div>
              </>
            )
          })
        }
      </div>
      </div>
      </>

    )


}

export default Api;
