import React, { useState } from 'react';
import axios from 'axios';

function Details() {

    const [details,setDetails]=useState([])
    const [no,setNo]= useState();


    const P_details=()=>{
        axios.get(`https://fakestoreapi.com/products/${no}`)
        .then((response)=>{
            console.log(response);

            setDetails(response)
        })
    }
  return (
    <>
       <input type="number"placeholder="Enter any number "  value={no} onChange={(e) => setNo(e.target.value)}/><br/><br/>
       <button className='button' onClick={P_details}>Click me</button>

       <div>
           <>
             <p>Id:  {details.id}</p><hr></hr>
             <p>AlbumId:  {details.title}</p><hr></hr>
             <p>Title:  {details.price}</p><hr></hr>
           </>


       </div>
       {

       }
    </>
  );

}

export default Details;
