import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../CSS/Style.css';
import { Link } from 'react-router-dom';

function Category() {



const[category,setCategory]=useState([]);

useEffect(()=>{
    axios.get(`https://fakestoreapi.com/products/categories`)
    .then((response)=>{
        console.log(response.data);
        setCategory(response.data);
    });
},[])

return(
    <div className='category'>

       <Link to={`/category/${category[0]}`} ><p className='button'>{category[0]}</p></Link><br></br>
       <Link to={`/category/${category[1]}`} ><p className='button'>{category[1]}</p></Link><br></br>
       <Link to={`/category/${category[2]}`} ><p className='button'>{category[2]}</p></Link><br></br>
       <Link to={`/${category[3]}`} ><p className='button'>{category[3]}</p></Link><br></br>

    </div>
);

}

export default Category;
